contracts/abstracts/RERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./Rarity.sol";
import "./Ownable.sol";
import "./Pausable.sol";

abstract contract RERC20 is Rarity, Ownable, Pausable {
  using SafeMath for uint256;
  string public name;
  string public symbol;
  uint256 public totalSupply = 0;
  uint8 public constant decimals = 18;

  mapping(uint256 => uint256) public balanceOf;
  mapping(uint256 => mapping(uint256 => uint256)) public transferAllowance;

  event Transfer(uint256 indexed from, uint256 indexed to, uint256 amount);
  event TransferApproval(
    uint256 indexed from,
    uint256 indexed to,
    uint256 amount
  );

  constructor() Rarity() Ownable() Pausable() {}

  function approve(
    uint256 from,
    uint256 spender,
    uint256 amount
  ) external returns (bool) {
    require(_isRarityApprovedOrOwner(from), "Must be owner");
    transferAllowance[from][spender] = amount;

    emit TransferApproval(from, spender, amount);
    return true;
  }

  function transfer(
    uint256 from,
    uint256 to,
    uint256 amount
  ) external returns (bool) {
    require(!_isPaused(), "Contract is paused");
    require(_isRarityApprovedOrOwner(from), "Must be owner");
    _transferTokens(from, to, amount);
    return true;
  }

  function transferFrom(
    uint256 executor,
    uint256 from,
    uint256 to,
    uint256 amount
  ) external returns (bool) {
    require(!_isPaused(), "Contract is paused");
    require(_isRarityApprovedOrOwner(executor), "Must be owner");
    uint256 spender = executor;
    uint256 allowance = transferAllowance[from][spender];
    require(amount <= allowance, "Transfer amount greater than approval");
    _transferTokens(from, to, amount);
    transferAllowance[from][spender] = allowance.sub(amount);
    return true;
  }

  function _transferTokens(
    uint256 from,
    uint256 to,
    uint256 amount
  ) internal {
    balanceOf[from] = balanceOf[from].sub(amount);
    balanceOf[to] = balanceOf[to].add(amount);

    emit Transfer(from, to, amount);
  }

  function setOwner(address _owner) external returns (bool) {
    require(_isOwner(msg.sender), "Must be owner");
    _setOwner(_owner);
    return true;
  }
}

contracts/abstracts/Mintable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./RERC20.sol";

abstract contract RarityMintable is RERC20 {
  using SafeMath for uint256;

  mapping(address => bool) public minters;

  event Mint(uint256 indexed from, uint256 indexed to, uint256 amount);

  function _mint(uint256 dst, uint256 amount) internal {
    uint256 balance = balanceOf[dst];
    totalSupply = totalSupply.add(amount);
    balanceOf[dst] = balance.add(amount);
    emit Mint(dst, dst, amount);
  }

  function _addMinter(address minter) internal {
    minters[minter] = true;
  }

  function _removeMinter(address minter) internal {
    minters[minter] = false;
  }
}

contracts/abstracts/Burnable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./RERC20.sol";

abstract contract RarityBurnable is RERC20 {
  using SafeMath for uint256;

  mapping(address => mapping(uint256 => uint256)) public burnAllowance;

  event Burn(uint256 indexed from, uint256 indexed to, uint256 amount);
  event BurnApproval(uint256 indexed from, address indexed to, uint256 amount);

  function _burnApprove(
    uint256 from,
    address burner,
    uint256 amount
  ) internal returns (bool) {
    burnAllowance[burner][from] = amount;
    emit BurnApproval(from, burner, amount);
    return true;
  }

  function _burn(uint256 dst, uint256 amount) internal {
    uint256 balance = balanceOf[dst];
    require(balance >= amount, "Balance too low");
    totalSupply = totalSupply.sub(amount);
    balanceOf[dst] = balance.sub(amount);
    emit Burn(dst, dst, amount);
  }
}

contracts/abstracts/Rarity.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

interface IRarity {
  function level(uint256) external view returns (uint256);

  function getApproved(uint256) external view returns (address);

  function ownerOf(uint256) external view returns (address);

  function summoner(uint256)
    external
    view
    returns (
      uint256,
      uint256,
      uint256,
      uint256
    );

  function adventure(uint256) external;
}

interface IRarityAttributes {
  function ability_scores(uint256)
    external
    view
    returns (
      uint32,
      uint32,
      uint32,
      uint32,
      uint32,
      uint32
    );
}

interface IRarityRandomCodex {
  function d20(uint256) external view returns (uint256);
}

abstract contract Rarity {
  IRarity constant _rm = IRarity(0xce761D788DF608BD21bdd59d6f4B54b2e27F25Bb);
  IRarityAttributes constant _attr =
    IRarityAttributes(0xB5F5AF1087A8DA62A23b08C00C6ec9af21F397a1);
  IRarityRandomCodex constant _random =
    IRarityRandomCodex(0x7426dBE5207C2b5DaC57d8e55F0959fcD99661D4);

  constructor() {}

  function _isRarityOwner(uint256 _summoner) internal view returns (bool) {
    return _getRarity().ownerOf(_summoner) == msg.sender;
  }

  function _isRarityApproved(uint256 _summoner) internal view returns (bool) {
    return _getRarity().getApproved(_summoner) == msg.sender;
  }

  function _isRarityApprovedOrOwner(uint256 _summoner)
    internal
    view
    returns (bool)
  {
    return _isRarityApproved(_summoner) || _isRarityOwner(_summoner);
  }

  function _getRarity() internal pure returns (IRarity) {
    return _rm;
  }

  function _getRarityAttributes() internal pure returns (IRarityAttributes) {
    return _attr;
  }

  function _getRarityRandom() internal pure returns (IRarityRandomCodex) {
    return _random;
  }

  function _getSummonerAttributes(uint256 _summoner)
    internal
    view
    returns (uint32[6] memory)
  {
    (
      uint32 _str,
      uint32 _dex,
      uint32 _con,
      uint32 _int,
      uint32 _wis,
      uint32 _cha
    ) = _getRarityAttributes().ability_scores(_summoner);
    uint32[6] memory scores = [_str, _dex, _con, _int, _wis, _cha];
    return scores;
  }

  function _getSummoner(uint256 _summoner)
    internal
    view
    returns (uint256[4] memory)
  {
    (uint256 _xp, uint256 _log, uint256 _class, uint256 _level) = _getRarity()
      .summoner(_summoner);
    uint256[4] memory result = [_xp, _log, _class, _level];
    return result;
  }
}

contracts/abstracts/Ownable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

abstract contract Ownable {
  address public owner;

  constructor() {
    owner = msg.sender;
  }

  function _isOwner(address addr) internal view returns (bool) {
    return owner == addr;
  }

  function _setOwner(address addr) internal returns (bool) {
    owner = addr;
    return true;
  }
}

contracts/abstracts/Pausable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

abstract contract Pausable {
  bool public paused;
  mapping(address => bool) public pausers;

  constructor() {
    paused = false;
    pausers[msg.sender] = true;
  }

  function _isPaused() internal view returns (bool) {
    return paused == true;
  }

  function pause() external returns (bool) {
    require(pausers[msg.sender], "Pause denied");
    _pause();
    return true;
  }

  function unpause() external returns (bool) {
    require(pausers[msg.sender], "Unpause denied");
    _unpause();
    return true;
  }

  function _addPauser(address addr) internal returns (bool) {
    pausers[addr] = true;
    return true;
  }

  function _removePauser(address addr) internal returns (bool) {
    pausers[addr] = false;
    return true;
  }

  function _pause() internal {
    paused = true;
  }

  function _unpause() internal {
    paused = false;
  }
}
