pragma solidity ^0.8.7;

abstract contract Ownable {
  address public owner;

  constructor() {
    owner = msg.sender;
  }

  function _isOwner(address addr) internal view returns (bool) {
    return owner == addr;
  }

  function _setOwner(address addr) internal returns (bool) {
    owner = addr;
    return true;
  }
}
